import React from 'react';
import { useForm } from '../util/hooks';
import { useMutation } from '@apollo/client';
import { CREATE_HAIKU_MUTATION } from '../graphql/Mutations';
import { Input, Button } from 'react-native-elements';

function HaikuForm () {

  const { values, onChange, onSubmit } = useForm(createHaikuCallback, {
    content: ''
  });

  const [createHaiku, { error }] = useMutation(CREATE_HAIKU_MUTATION, {
    variables: values,
    update(_, result) {
      console.log(result);
      values.content = '';
    }
  });

  function createHaikuCallback() {
    createHaiku();
  }

  return (
    <form className="create-haiku-form">
      <h2 className="create-haiku-title">
        Create haiku:
      </h2>
      <Input placeholder="Enter your first line..." name='content' value={values.content} 
      />
      <Input placeholder="Enter your second line..." name='content' value={values.content}

      />
      <Input placeholder="Enter your third line..." name='content' value={values.content}

      />
      <Button type="solid"
        title="Publish haiku"
      />
    </form>
  )
}


export default HaikuForm;
