import React from 'react';
import { FETCH_HAIKUS_QUERY } from '../graphql/Queries';
import { useQuery } from '@apollo/client';
import { StyleSheet, Text, View } from 'react-native';
import { AppLoading } from 'expo';
import * as Font from 'expo-font';


let customFonts = {
  'SF-Pro-Display-Regular': require('./assets/fonts/SF-Pro-Display-Regular.otf'),
};



const fetchFonts = () => {
  return Font.loadAsync({
    sf_pro_display_regular: require('./assets/fonts/SF-Pro-Display-Regular.otf')
  });
};

function Home() {


  const [dataLoaded, setDataLoaded] = useState(false);

  if (!dataLoaded) {
    return (
      <AppLoading
        startAsync={fetchFonts}
        onFinish={() => setDataLoaded(true)}
      />
    );
  }



  const { data, loading, error } = useQuery(FETCH_HAIKUS_QUERY);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;
  if (data) console.log(data);

  return (
    <section>
    <View style={{ fontflex: 1, alignItems: 'center', justifyContent: 'center', color: '#60BADA' }}>
    {/* <Text style={{ fontFamily: 'sf_pro_display_regular' }}> */}
      <ul>
        {data.haikus.map( haiku => {
          return <li key={haiku.id}>{haiku.content}</li>
        })}
      </ul>
      <div className="home-flex">
        <h1>
          O 
          <br />
          K 
          <br />
          U
        </h1>
      </div>
      <div className="form-flex">
        <div>

          <form>
            <label>Email</label>
            <input placeholder="Enter email...">

            </input>
            <label>Password</label>
            <input placeholder="Enter password...">

            </input>
            <button>
              Sign in
            </button>
          </form>
        </div>
      </div>
      {/* </Text> */}
        </View>
    </section>
  )
}

export default Home;
