import React from 'react';
import { FETCH_HAIKUS_QUERY } from '../graphql/Queries';
import { useQuery } from '@apollo/client';
import { StyleSheet, Text, View } from 'react-native';
import { useFonts } from "@use-expo/font";
import { AppLoading } from 'expo';
import * as Font from 'expo-font';


function Home() {


  const [isLoaded] = useFonts({
    "SF-Pro-Display-Regular": require("../assets/fonts/SFProDisplay-Regular.otf"),
  });



  const { data, loading, error } = useQuery(FETCH_HAIKUS_QUERY);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;
  if (data) console.log(data);

  if (!isLoaded) {
    return <AppLoading />;
  } else {
  return (
      <section>
      <View style={{ fontflex: 1, alignItems: 'center', justifyContent: 'center', color: '#60BADA' }}>
        <ul>
          {data.haikus.map( haiku => {
            return <li key={haiku.id}>{haiku.content}</li>
          })}
        </ul>
        <div className="home-flex">
        <Text style={{ fontFamily: "SF-Pro-Display-Regular" }}>
          <h1>
            O 
            <br />
            K 
            <br />
            U
          </h1>
          </Text>
        </div>
        <div className="form-flex">
          <div>

            <form>
              <label>Email</label>
              <input placeholder="Enter email...">

              </input>
              <label>Password</label>
              <input placeholder="Enter password...">

              </input>
              <button>
                Sign in
              </button>
            </form>
          </div>
        </div>
          </View>
      </section>
    )
        }
}

export default Home;
