import { gql } from '@apollo/client';

// place our app Query functions logic here

export const FETCH_HAIKUS_QUERY = gql`
query getHaikus {
  haikus {
    id 
    line1
    line2
    line3
    createdAt
    author {
      username
    }
  }
}
`

export const FETCH_USER_QUERY = gql`
query getUser($userEmail: String!, $userPassword: String!) {
    users(where: { email: $userEmail, password: $userPassword }) {
        username
        name
        email
        password
    }
}
`

export const FETCH_USER_BY_EMAIL = gql`
query getUserByEmail($userEmail: String!) {
    user(email: $userEmail) {
        username
        name
        email
        password
    }
}
`

