import React from 'react';
import { TouchableOpacity } from 'react-native';

import { View, Button, Text } from 'react-native';




function LandingScreen ({navigation}) {



  return (
    <View style={{fontSize: "6rem", flex: 1, justifyContent: "center", marginLeft: "auto", marginRight: "auto", width: "10em"}}>
      <View>
        <View style={{marginBottom: "1em", fontSize: "24px"}}>
    <TouchableOpacity
    onPress={() => navigation.navigate('Create')}
    style={{backgroundColor: '#20994C', width: "24rem", paddingTop: "0.6em", paddingBottom: "0.6em"}}
      >
      <Text style={{fontSize:32, color: '#F5F2EB', textAlign: "center", textTransform: "uppercase"}}>Create a haiku</Text>
      </TouchableOpacity>
  </View>
  <TouchableOpacity
    onPress={() => navigation.navigate('Feed')}
    style={{backgroundColor: '#20994C', width: "24rem", paddingTop: "0.6em", paddingBottom: "0.6em"}}
  >
    <Text style={{fontSize:32, color: '#F5F2EB', textAlign: "center", textTransform: "uppercase"}}>Feed</Text>
    </TouchableOpacity>
  <TouchableOpacity
    onPress={() => navigation.navigate('WordOfDay')}
  >
    <Text style={{fontSize:32, color: '#F5F2EB', textAlign: "center", textTransform: "uppercase"}}>Word of Day</Text>
    </TouchableOpacity>
    <TouchableOpacity
    title="My Collection"
    onPress={() => navigation.navigate('MyCollection')}
    color='#20994C'
  />
  <TouchableOpacity
    title="My Haikus"
    onPress={() => navigation.navigate('MyHaikus')}
    color='#20994C'
  />
    <TouchableOpacity
    title="Profile"
    onPress={() => navigation.navigate('Profile')}
    color='#20994C'
  />
  <TouchableOpacity
    title="Gallery"
    onPress={() => navigation.navigate('Gallery')}
    color='#20994C'
  />
        </View>
</View>

  )
}




export default LandingScreen;
