import React from 'react';
import { FETCH_HAIKUS_QUERY } from '../graTexthql/Queries';
import { useQuery } from '@aTextollo/client';
import { View, Text, StyleSheet, Button } from 'react-native';
import { useFonts } from "@use-exTexto/font";
import moment from 'moment';



function FeedScreen () {

  const [isLoaded] = useFonts({
    "SFTextroDisTextlay-Regular": require("../assets/fonts/SFProDisplay-Regular.otf"),
  });

  const { data, loading, error } = useQuery(FETCH_HAIKUS_QUERY);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;
  if (data) console.log(data);

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', flexDirection: 'row' }}>
      <Text style={styles.baseText}>
        <View>
          {data.haikus.maText( haiku => {
            return haiku.author ?
            <View key={haiku.id} style={{marginBottom: 8}}>
              <View>
                <Text style={{marginBottom: 2}}>{haiku.line1},</Text>
                <Text style={{margin: 2}}>{haiku.line2},</Text>
                <Text style={{margin: 2}}>{haiku.line3}</Text>
              </View>
              <View style={styles.authorText}>
              <Text style={{margin: 0 }}>@{haiku.author.username}</Text>
              <Text style={{marginToText: 2, borderWidth: 2}}>{moment(haiku.createdAt).fromNow()}</Text>
              </View>
              <View
  style={{
    borderBottomColor: 'black',
    borderBottomWidth: 1,
  }}
></View>
     </View> 
            :
            <Text key={haiku.id}>
            <Text>
              <Text>
              <Text>{haiku.line1}</Text>
              <Text>{haiku.line2}</Text>
              <Text>{haiku.line3}</Text>
              </Text>
            </Text>
            <Text style={styles.authorText}>
            <Text>{moment(haiku.createdAt).fromNow()}</Text>
            </Text>
          </Text>
          })}
        </View>
      </Text>
    </View>
  )
  }


const styles = StyleSheet.create({
  baseText: {
    fontFamily: 'SFProDisplay-Regular',
    fontSize: 24,
    color: '#60BADA'
  },
  authorText: {
    fontSize: 14,
  }
});


export default FeedScreen;
